using Microsoft.EntityFrameworkCore;using Microsoft.EntityFrameworkCore.Metadata.Builders;using NT.SHARED.Models;namespace NT.SHARED.Configurations{ public class CartConfiguration: IEntityTypeConfiguration<Cart>{ public void Configure(EntityTypeBuilder<Cart> builder){ builder.ToTable("Cart"); builder.HasKey(x=>x.Id); builder.HasOne(x=>x.Customer).WithOne(c=>c.Cart).HasForeignKey<Cart>(x=>x.CustomerId); } } }