using Microsoft.EntityFrameworkCore;using Microsoft.EntityFrameworkCore.Metadata.Builders;using NT.SHARED.Models;namespace NT.SHARED.Configurations{ public class CartDetailConfiguration: IEntityTypeConfiguration<CartDetail>{ public void Configure(EntityTypeBuilder<CartDetail> builder){ builder.ToTable("CartDetail"); builder.HasKey(x=> new { x.CartId, x.ProductDetailId }); builder.Property(x=>x.Quantity).IsRequired(); builder.HasOne(x=>x.Cart).WithMany(c=>c.Items).HasForeignKey(x=>x.CartId); builder.HasOne(x=>x.ProductDetail).WithMany(d=>d.CartDetails).HasForeignKey(x=>x.ProductDetailId); } } }