using Microsoft.EntityFrameworkCore;using Microsoft.EntityFrameworkCore.Metadata.Builders;using NT.SHARED.Models;namespace NT.SHARED.Configurations{ public class ColorConfiguration: IEntityTypeConfiguration<Color>{ public void Configure(EntityTypeBuilder<Color> builder){ builder.ToTable("Color"); builder.HasKey(x=>x.Id); builder.Property(x=>x.Name).IsRequired().HasMaxLength(100); builder.Property(x=>x.HexCode).HasMaxLength(7); } } }