using Microsoft.EntityFrameworkCore;using Microsoft.EntityFrameworkCore.Metadata.Builders;using NT.SHARED.Models;namespace NT.SHARED.Configurations{ public class ElasticityConfiguration: IEntityTypeConfiguration<Elasticity>{ public void Configure(EntityTypeBuilder<Elasticity> builder){ builder.ToTable("Elasticity"); builder.HasKey(x=>x.Id); builder.Property(x=>x.Name).IsRequired().HasMaxLength(100); builder.Property(x=>x.Description).HasMaxLength(250); } } }