using Microsoft.EntityFrameworkCore;using Microsoft.EntityFrameworkCore.Metadata.Builders;using NT.SHARED.Models;namespace NT.SHARED.Configurations{ public class HardnessConfiguration: IEntityTypeConfiguration<Hardness>{ public void Configure(EntityTypeBuilder<Hardness> builder){ builder.ToTable("Hardness"); builder.HasKey(x=>x.Id); builder.Property(x=>x.Name).IsRequired().HasMaxLength(100); builder.Property(x=>x.Description).HasMaxLength(250); } } }