using Microsoft.EntityFrameworkCore;using Microsoft.EntityFrameworkCore.Metadata.Builders;using NT.SHARED.Models;namespace NT.SHARED.Configurations{ public class LengthConfiguration: IEntityTypeConfiguration<Length>{ public void Configure(EntityTypeBuilder<Length> builder){ builder.ToTable("Length"); builder.HasKey(x=>x.Id); builder.Property(x=>x.Name).IsRequired().HasMaxLength(100); builder.Property(x=>x.Description).HasMaxLength(250); } } }