using Microsoft.EntityFrameworkCore;using Microsoft.EntityFrameworkCore.Metadata.Builders;using NT.SHARED.Models;namespace NT.SHARED.Configurations{ public class OrderDetailConfiguration: IEntityTypeConfiguration<OrderDetail>{ public void Configure(EntityTypeBuilder<OrderDetail> builder){ builder.ToTable("OrderDetail"); builder.HasKey(x=>x.Id); builder.Property(x=>x.UnitPrice).HasColumnType("decimal(18,2)"); builder.Property(x=>x.TotalPrice).HasColumnType("decimal(18,2)"); builder.Property(x=>x.DiscountPercent).HasColumnType("decimal(18,2)"); builder.HasOne(x=>x.Order).WithMany(o=>o.Details).HasForeignKey(x=>x.OrderId); builder.HasOne(x=>x.ProductDetail).WithMany(pd=>pd.OrderDetails).HasForeignKey(x=>x.ProductDetailId); } } }