using Microsoft.EntityFrameworkCore;using Microsoft.EntityFrameworkCore.Metadata.Builders;using NT.SHARED.Models;namespace NT.SHARED.Configurations{ public class ProductImageConfiguration: IEntityTypeConfiguration<ProductImage>{ public void Configure(EntityTypeBuilder<ProductImage> builder){ builder.ToTable("ProductImage"); builder.HasKey(x=>x.Id); builder.Property(x=>x.ImageUrl).IsRequired().HasMaxLength(300); builder.HasOne(x=>x.ProductDetail).WithMany(d=>d.Images).HasForeignKey(x=>x.ProductDetailId); } } }