using Microsoft.EntityFrameworkCore;using Microsoft.EntityFrameworkCore.Metadata.Builders;using NT.SHARED.Models;namespace NT.SHARED.Configurations{ public class SurfaceFinishConfiguration: IEntityTypeConfiguration<SurfaceFinish>{ public void Configure(EntityTypeBuilder<SurfaceFinish> builder){ builder.ToTable("SurfaceFinish"); builder.HasKey(x=>x.Id); builder.Property(x=>x.Name).IsRequired().HasMaxLength(100); builder.Property(x=>x.Description).HasMaxLength(250); } } }